document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".toggle-header");e.forEach(e=>{e.addEventListener("click",()=>{const n=e.getAttribute("data-target"),t=document.getElementById(n);t.classList.contains("hidden")?(t.classList.remove("hidden"),t.classList.add("visible"),e.classList.add("active")):(t.classList.remove("visible"),t.classList.add("hidden"),e.classList.remove("active"))})})})